function palindrome(str) {

  str=str.match(/[a-z0-9]/ig);
  let originalString=str.join('').toLowerCase();
  let reverseString=str=str.reverse().join('').toLowerCase();

  if(originalString===reverseString)
    return true;

  return false;
}



